<body>
	<div class="ng-star-inserted">
		<div class="ng-star-inserted">

			<div  class="bumz-auth ng-pristine ng-invalid ng-touched" >

				<i class="pi pi-logo"></i>
				<div class="auth-form">
					<div class="auth-error ng-star-inserted">
						<p class="ng-star-inserted show_arror" ></p>
					</div>
					<input placeholder="Логин или электронная почта" type="text" class="p-inputtext p-component p-element ng-pristine ng-invalid ng-touched p_login">

					<div class="ng-tns-c934500024-1 p-password p-component p-inputwrapper p-input-icon-right">

						<input pinputtext="" class="p-inputtext p-component p-element ng-tns-c934500024-1 p-password-input p_password" type="password" placeholder="Пароль"><!----><!---->

					</div>

					<button type="submit" label="Войти" class="p-element p-button p-component add_login" >
						<span class="p-button-label">Войти</span>
					</button>
				</div>
				<div class="auth-navigation-block">
					<a href="/auth/invite">У меня есть инвайт</a>
				</div>
			</form>

		</div>
	</div>
	<script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.7.1/jquery.min.js" integrity="sha512-v2CJ7UaYy4JwqLDIrZUI/4hqeoQieOmAZNXBeQyjo21dadnwR+8ZaIJVT8EE2iyI61OV8e6M8PP2/4hpQINQ/g==" crossorigin="anonymous" referrerpolicy="no-referrer"></script>
	<script>
		$('.add_login').on('click', function(e){
			e.preventDefault();
			let login = $('.p_login').val();
			let password = $('.p_password').val();

			if(login.length <= 0) {
				$('.show_arror').text('Логин не должен быть пустым');

			}else{
				if(password.length <= 0) {
					$('.show_arror').text('Пароль не должен быть пустым');
				}else{
					$.ajax({
								url: "/auth",
								type:"POST",
								data:{
									"_token":  "{{csrf_token()}}",
									login:login,
									password:password,
								},
								success:function(response){
									if(response == 1){
										alert('Вы успешно авторизировались!');
										document.location.href = '/';
									}else{
										$('.show_arror').text('Некорректные данные!');
									}
								},
							});
				}
			}
			
			
		});
							
	</script>
</body>
</html>